import React, {useContext, useState, useEffect} from 'react'
import {GlobalState} from '../../../GlobalState'
import{Link} from 'react-router-dom'
import axios from 'axios'
import PayPal from './PayPal'

function Cart() {
  const state = useContext(GlobalState)
  const [cart, setCart] = state.userAPI.cart
  const [token] = state.token
  const [total, setTotal] = useState(0)

  useEffect(() =>{
    const getTotal = () =>{
        const total = cart.reduce((prev, item) => {
          return prev + (item.price * 0.02 * item.frequency)
        },0)
        setTotal(total)
    }
    getTotal()

  },[cart])


const addToCart = async () =>{
  await axios.patch('/user/addcart', {cart},{
    headers: {Authorization: token}
  })
}


  const increment = (id) =>{
    cart.forEach(item => {
        if(item._id === id){
            item.frequency === 3 ? item.frequency = 3 : item.frequency += 1
        }
    })

    setCart([...cart]) 
    addToCart()  
}

  const decrement = (id) =>{
    cart.forEach(item => {
        if(item._id === id){
            item.frequency === 1 ? item.frequency = 1 : item.frequency -= 1
        }
    })

    setCart([...cart])
    addToCart()
  }

  const removeProduct = id =>{
    if(window.confirm("Do you want to delete this property?")){
        cart.forEach((item, index) => {
            if(item._id === id){
                cart.splice(index, 1)
            }
        })

        setCart([...Cart])
        addToCart()
    }
  }

  if(cart.length === 0) 
        return <h2 style={{textAlign: "center", fontSize: "5rem"}}>Cart Empty</h2>   
        
  return (
    <div>
       {
          cart.map(product => (
            <div className="detail cart" key={product._id}>
                <img src={product.images.url} alt="" />
                
                <div className="box-detail">
                    <h2>{product.title}</h2>

                    <h3>$ {product.price }</h3>
                    <p>{product.description}</p>
                    <p>{product.content}</p>
                    
                    <div className="amount">
                      <button onClick={() => decrement(product._id)}> - </button>
                      <span>{product.frequency}</span>
                      <button onClick={() => increment(product._id)}> + </button>
                    </div>

                    <div className="delete" 
                    onClick={() => removeProduct(product._id)}>
                      X
                    </div>
                  
                    <div class="input_wrap">
                      <label for="bamount">Bidding Amount 1</label>&nbsp;&nbsp;&nbsp;&nbsp;
                      <span>$</span><input type="number" id="bamount"/>
                    </div>

                    <div class="input_wrap">
                      <label for="bamount">Bidding Amount 2</label>&nbsp;&nbsp;&nbsp;&nbsp;
                      <span>$</span><input type="number" id="bamount"/>
                    </div>

                    <div class="input_wrap">
                      <label for="bamount">Bidding Amount 3</label>&nbsp;&nbsp;&nbsp;&nbsp;
                      <span>$</span><input type="number" id="bamount"/>
                    </div>
                      
                   
                </div>
            </div>
          ))
       }  

       <div className="total">
          <h3>Total: $ {total}</h3>
          <PayPal/>   
      </div> 
    </div>
  )
}

export default Cart
